parameters.jsCode:
// Single Code node: per-email Telegram messages + final daily summary
// Parses text format from LLM and combines with email metadata from previous nodes

// ---------- helpers ----------

/**
 * Parses text response from LLM into structured data
 * @param {string} text - Raw text response from LLM
 * @returns {Object|null} Parsed data or null on failure
 */
function parseTextResponse(text) {
  if (!text || typeof text !== 'string') return null;

  const result = {
    isImportant: false,
    category: 'unknown',
    summary: '',
    actions: []
  };

  const lines = text.trim().split('\n');
  let currentField = null;
  let inActions = false;

  for (let line of lines) {
    line = line.trim();

    // Check for end marker
    if (line === '---') break;

    // Parse field lines
    if (line.startsWith('Important:')) {
      const value = line.substring('Important:'.length).trim().toLowerCase();
      result.isImportant = value === 'yes' || value === 'true';
      inActions = false;
      currentField = null;
    } else if (line.startsWith('Category:')) {
      result.category = line.substring('Category:'.length).trim();
      inActions = false;
      currentField = null;
    } else if (line.startsWith('Summary:')) {
      result.summary = line.substring('Summary:'.length).trim();
      currentField = 'summary';
      inActions = false;
    } else if (line.startsWith('Actions:')) {
      inActions = true;
      currentField = null;
    } else if (inActions && line.startsWith('-')) {
      // Parse action line
      const action = line.substring(1).trim();
      if (action && action.toLowerCase() !== 'none') {
        result.actions.push(action);
      }
    } else if (currentField === 'summary' && line && !inActions) {
      // Continue multi-line summary
      result.summary += ' ' + line;
    }
  }

  return result;
}

/**
 * Cleans and normalizes sender name from email
 * @param {string} senderName - Raw sender name from email
 * @returns {string} Cleaned sender name
 */
function cleanFromName(senderName) {
  if (!senderName) return "Unknown sender";

  let cleaned = String(senderName).trim();

  // Remove email address in angle brackets
  if (cleaned.includes("<")) {
    cleaned = cleaned.split("<")[0].trim();
  }

  // Remove domain after pipe
  if (cleaned.includes("|")) {
    cleaned = cleaned.split("|")[0].trim();
  }

  // Remove surrounding quotes
  cleaned = cleaned.replace(/^[\"']|[\"']$/g, "");

  return cleaned || "Unknown sender";
}

/**
 * Formats action items into markdown list with proper link handling
 * @param {Array<string>} actions - Array of action strings
 * @returns {string} Formatted actions block or empty string
 */
function formatActions(actions) {
  if (!Array.isArray(actions) || actions.length === 0) {
    return "";
  }

  const URL_REGEX = /https?:\/\/\S+/;

  const formattedActions = actions
    .map((action) => {
      // Handle "Label: URL" format
      if (action.includes(":")) {
        const parts = action.split(":");
        const label = parts[0].trim();
        const rest = parts.slice(1).join(":").trim();
        const urlMatch = rest.match(URL_REGEX);

        if (urlMatch) {
          return `- [${label}](${urlMatch[0]})`;
        }
      }

      // Handle direct URL in text
      const urlMatch = action.match(URL_REGEX);
      if (urlMatch) {
        return `- [Open link](${urlMatch[0]})`;
      }

      // Plain text action
      return `- ${action}`;
    })
    .filter(Boolean);

  return formattedActions.length > 0
    ? `\n\nActions:\n${formattedActions.join("\n")}`
    : "";
}

/**
 * Creates formatted email message
 * @param {Object} data - Combined email data
 * @returns {string} Formatted message string
 */
function createEmailMessage(data) {
  const { from, subject, category, receivedDate, summary, actions, gmailUrl } = data;
  const actionsBlock = formatActions(actions);
  const formattedDate = new Date(receivedDate).toLocaleString();

  return `[${from}]: ${subject}\nCategory: ${category}\nReceived: ${formattedDate}\n\n${summary}${actionsBlock}\n\n[Open in Gmail](${gmailUrl})`;
}

// ---------- main ----------
const items = $input.all();

// Early validation
if (!items || !Array.isArray(items)) {
  console.error("Invalid input: Expected array of items");
  return [
    {
      json: {
        message: "‚ùå Error: Invalid input data provided to email formatter",
        error: true,
        isSummary: true,
      },
    },
  ];
}

const perEmailOutputs = [];
const aggregationStats = {
  total: 0,
  important: 0,
  byCategory: new Map(),
  importantList: [],
  errors: 0,
  parsingFailures: 0,
};

for (const item of items) {
  try {
    // Handle both formats: items with or without .json wrapper
    const data = item.json || item;

    console.log("Processing item:", JSON.stringify(data).substring(0, 200));

    // Get LLM response text
    const llmResponse = data.response;

    if (!llmResponse) {
      console.warn("Skipping item with no LLM response. Available keys:", Object.keys(data));
      aggregationStats.errors++;
      continue;
    }

    // Parse LLM text response
    const parsedData = parseTextResponse(llmResponse);

    if (!parsedData) {
      console.error(`Failed to parse LLM response. Preview: ${llmResponse.substring(0, 100)}...`);
      aggregationStats.parsingFailures++;
      aggregationStats.errors++;
      continue;
    }

    // Get email metadata
    const from = cleanFromName(data.fromName || "Unknown sender");
    const subject = data.subject || "No subject";
    const gmailUrl = data.gmailUrl || "#";
    const receivedDate = data.internalDate || new Date().toISOString();

    // Combine LLM analysis with email metadata
    const combinedData = {
      isImportant: parsedData.isImportant,
      category: parsedData.category || "unknown",
      summary: parsedData.summary || "No summary available",
      actions: parsedData.actions || [],
      from,
      subject,
      gmailUrl,
      receivedDate,
    };

    // Update aggregation stats
    aggregationStats.total++;
    if (combinedData.isImportant) {
      aggregationStats.important++;
      aggregationStats.importantList.push({
        subject: combinedData.subject,
        from: combinedData.from,
      });
    }

    // Track categories
    const currentCount = aggregationStats.byCategory.get(combinedData.category) || 0;
    aggregationStats.byCategory.set(combinedData.category, currentCount + 1);

    // Create formatted message
    const message = createEmailMessage(combinedData);

    const outputItem = {
      json: {
        message,
        isImportant: combinedData.isImportant,
        subject: combinedData.subject,
        from: combinedData.from,
        category: combinedData.category,
        receivedDate: combinedData.receivedDate,
        gmailUrl: combinedData.gmailUrl,
        summary: combinedData.summary,
        actions: combinedData.actions,
        isSummary: false,
      },
    };

    perEmailOutputs.push(outputItem);
  } catch (error) {
    console.error("Error processing email item:", error);
    aggregationStats.errors++;
  }
}

/**
 * Creates daily summary message from aggregation stats
 * @param {Object} stats - Aggregation statistics
 * @returns {Object} Summary message object
 */
function createDailySummary(stats) {
  const finishedAt = new Date().toLocaleString();

  // Handle no emails case
  if (stats.total === 0) {
    return {
      message: "üìß Daily Gmail scan complete ‚Äî no new emails found.",
      totalEmails: 0,
      importantEmails: 0,
      byCategory: {},
      errors: stats.errors,
      parsingFailures: stats.parsingFailures,
      finishedAt,
      isSummary: true,
    };
  }

  // Build category breakdown
  const categoryEntries = Array.from(stats.byCategory.entries())
    .sort(([, a], [, b]) => b - a)
    .map(([category, count]) => `‚Ä¢ ${category}: ${count}`)
    .join("\n");

  // Build top important emails list
  const topImportantLines = stats.importantList
    .slice(0, 5)
    .map((email) => `- **${email.subject}** ‚Äî ${email.from}`)
    .join("\n");

  // Construct summary message
  let summaryMessage = `üìä **Daily Gmail Summary**\n\nüìß Total emails processed: ${stats.total}\nüî¥ Important emails: ${stats.important}`;

  if (stats.errors > 0) {
    summaryMessage += `\n‚ö†Ô∏è Processing errors: ${stats.errors}`;
  }

  if (stats.parsingFailures > 0) {
    summaryMessage += `\n‚ö†Ô∏è Parsing failures: ${stats.parsingFailures}`;
  }

  if (categoryEntries) {
    summaryMessage += `\n\n**By category:**\n${categoryEntries}`;
  }

  if (topImportantLines) {
    summaryMessage += `\n\n**Top important:**\n${topImportantLines}`;
  }

  summaryMessage += `\n\n‚úÖ Individual summaries were sent above.\n\n_Scan completed at ${finishedAt}_`;

  return {
    message: summaryMessage,
    totalEmails: stats.total,
    importantEmails: stats.important,
    byCategory: Object.fromEntries(stats.byCategory),
    errors: stats.errors,
    parsingFailures: stats.parsingFailures,
    finishedAt,
    isSummary: true,
  };
}

// Create and append daily summary
const summary = createDailySummary(aggregationStats);
const summaryItem = {
  json: summary,
};
perEmailOutputs.push(summaryItem);

return perEmailOutputs;

