parameters.jsCode:
// Calculate processing metrics after LLM response

// Get start time from the "Add Start Timestamp" node
const startTimeMs = $json.startTimeMs;
const endTimeMs = Date.now();
const durationMs = endTimeMs - startTimeMs;
const durationSec = Math.round(durationMs / 1000);
const durationMin = (durationSec / 60).toFixed(2);

// Extract LLM metrics from Ollama response
const promptTokens = $json.prompt_eval_count || 0;
const outputTokens = $json.eval_count || 0;
const totalTokens = promptTokens + outputTokens;

// Calculate tokens per second using Ollama's timing data
const promptEvalDuration = $json.prompt_eval_duration || 1;
const evalDuration = $json.eval_duration || 1;
const promptTokensPerSec = (promptTokens / (promptEvalDuration / 1000000000)).toFixed(2);
const outputTokensPerSec = (outputTokens / (evalDuration / 1000000000)).toFixed(2);

// Format duration for display
let durationDisplay;
if (durationSec < 60) {
  durationDisplay = `${durationSec}s`;
} else if (durationSec < 3600) {
  durationDisplay = `${durationMin}m`;
} else {
  const hours = Math.floor(durationSec / 3600);
  const mins = Math.floor((durationSec % 3600) / 60);
  durationDisplay = `${hours}h ${mins}m`;
}

// Get email metadata from Loop Over Emails node
const fromName = $json.fromName;
const subject = $json.subject;

return {
  json: {
    response: $json.response,
    fromName,
    subject,
    durationMs,
    durationSec,
    durationMin,
    durationDisplay,
    promptTokens,
    outputTokens,
    totalTokens,
    promptTokensPerSec,
    outputTokensPerSec,
    // Include original timing data from Ollama (in seconds)
    totalDurationSec: (($json.total_duration || 0) / 1000000000).toFixed(2),
    loadDurationSec: (($json.load_duration || 0) / 1000000000).toFixed(2),
    promptEvalDurationSec: (($json.prompt_eval_duration || 0) / 1000000000).toFixed(2),
    evalDurationSec: (($json.eval_duration || 0) / 1000000000).toFixed(2)
  }
};
