// Calculate processing metrics after LLM response
const items = $input.all();

return items.map(item => {
  const startTimeMs = item.json.startTimeMs;
  const endTimeMs = Date.now();
  const durationMs = endTimeMs - startTimeMs;
  const durationSec = Math.round(durationMs / 1000);
  const durationMin = (durationSec / 60).toFixed(2);

  // Extract LLM metrics from response
  const llmResponse = item.json.response || '';
  const promptTokens = item.json.prompt_eval_count || 0;
  const outputTokens = item.json.eval_count || 0;
  const totalTokens = promptTokens + outputTokens;

  // Format duration for display
  let durationDisplay;
  if (durationSec < 60) {
    durationDisplay = `${durationSec}s`;
  } else if (durationSec < 3600) {
    durationDisplay = `${durationMin}m`;
  } else {
    const hours = Math.floor(durationSec / 3600);
    const mins = Math.floor((durationSec % 3600) / 60);
    durationDisplay = `${hours}h ${mins}m`;
  }

  return {
    json: {
      ...item.json,
      durationMs,
      durationSec,
      durationMin,
      durationDisplay,
      promptTokens,
      outputTokens,
      totalTokens
    }
  };
});

