// Single Code node: per-email Telegram messages + final daily summary
// Uses ONLY the JSON inside `response` for each input item.

// ---------- helpers ----------
function toObj(r) {
  if (r == null) return {};
  if (typeof r === 'string') { try { return JSON.parse(r); } catch { return {}; } }
  if (typeof r === 'object') return r;
  return {};
}

function cleanFromName(s) {
  if (!s) return 'Unknown sender';
  let out = String(s);
  if (out.includes('<')) out = out.split('<')[0].trim();
  if (out.includes('|')) out = out.split('|')[0].trim();
  return out.replace(/(^"|"$)/g, '') || 'Unknown sender';
}

function formatActions(actions) {
  if (!Array.isArray(actions) || actions.length === 0) return '';
  const rows = actions.map((a) => {
    if (typeof a === 'string') {
      // Try "Label: URL"
      const parts = a.split(':');
      if (parts.length >= 2) {
        const label = parts.shift().trim();
        const rest = parts.join(':').trim();
        const m = rest.match(/https?:\/\/\S+/);
        if (m) return `- [${label}](${m[0]})`;
      }
      const m = a.match(/https?:\/\/\S+/);
      if (m) return `- [Open link](${m[0]})`;
      return `- ${a}`;
    } else if (a && typeof a === 'object') {
      const label = a.label || a.title || 'Open link';
      const url = a.url || a.href;
      if (url) return `- [${label}](${url})`;
      return `- ${label}`;
    }
    return null;
  }).filter(Boolean);
  return rows.length ? `\n**Actions:**\n${rows.join('\n')}` : '';
}

// ---------- main ----------
const items = $input.all();
const perEmailOutputs = [];
const agg = {
  total: 0,
  important: 0,
  byCategory: {},               // { category: count }
  importantList: [],            // collect important subjects for "top important"
};

for (const it of items) {
  const env = toObj(it.json?.response); // <-- only source of truth

  const isImportant = !!env.isImportant;
  const from = cleanFromName(env.from || 'Unknown sender');
  const subject = env.subject || 'No subject';
  const category = env.category || 'Uncategorized';
  const receivedDate = env.receivedDate || new Date().toISOString();
  const gmailUrl = env.gmailUrl || '#';
  const summary = env.summary || 'No summary';
  const actions = Array.isArray(env.actions) ? env.actions : [];

  // Aggregate
  agg.total += 1;
  if (isImportant) agg.important += 1;
  agg.byCategory[category] = (agg.byCategory[category] || 0) + 1;
  if (isImportant) {
    agg.importantList.push({ subject, from });
  }

  // Per-email message
  const priorityEmoji = isImportant ? 'ðŸ”´' : 'ðŸ“§';
  const actionsBlock = formatActions(actions);

  const message =
    `${priorityEmoji} **Email Summary**\n\n` +
    `**From:** ${from}\n` +
    `**Subject:** ${subject}\n` +
    `**Category:** ${category}\n` +
    `**Received:** ${new Date(receivedDate).toLocaleString()}\n\n` +
    `**Summary:**\n${summary}${actionsBlock}\n\n` +
    `[Open in Gmail](${gmailUrl})`;

  perEmailOutputs.push({
    json: {
      message,
      isImportant,
      subject,
      from,
      category,
      receivedDate,
      gmailUrl,
      summary,
      actions,
      isSummary: false,
    },
  });
}

// If no emails, return just a "no new emails" summary
if (agg.total === 0) {
  return [{
    json: {
      message: 'ðŸ“§ Daily Gmail scan complete â€” no new emails found.',
      totalEmails: 0,
      importantEmails: 0,
      byCategory: {},
      finishedAt: new Date().toLocaleString(),
      isSummary: true,
    },
  }];
}

// Build final daily summary message
const catLines = Object.entries(agg.byCategory)
  .sort((a, b) => b[1] - a[1])
  .map(([cat, n]) => `â€¢ ${cat}: ${n}`)
  .join('\n');

const topImportantLines = agg.importantList
  .slice(0, 5)
  .map(e => `- **${e.subject || 'No subject'}** â€” ${e.from || 'Unknown sender'}`)
  .join('\n');

const finishedAt = new Date().toLocaleString();

let summaryMessage =
  `ðŸ“Š **Daily Gmail Summary**\n\n` +
  `ðŸ“§ Total emails processed: ${agg.total}\n` +
  `ðŸ”´ Important emails: ${agg.important}\n`;

if (catLines) {
  summaryMessage += `\n**By category:**\n${catLines}\n`;
}
if (topImportantLines) {
  summaryMessage += `\n**Top important:**\n${topImportantLines}\n`;
}

summaryMessage += `\nâœ… Individual summaries were sent above.\n\n` +
                  `_Scan completed at ${finishedAt}_`;

// Append summary as the last item
perEmailOutputs.push({
  json: {
    message: summaryMessage,
    totalEmails: agg.total,
    importantEmails: agg.important,
    byCategory: agg.byCategory,
    finishedAt,
    isSummary: true,
  },
});

return perEmailOutputs;
